INSERT INTO day_cards (date, slug, title, body_md, spotlight_json, tags, hero_url) VALUES
  ('2026-04-16', 'mimosa-sativa-dominant', 'Mimosa (Sativa-Dominant)', 'Named after the brunch cocktail, Mimosa blends Clementine and Purple Punch for a citrusy, uplifting high that''s perfect for social gatherings and creative projects.
INSERT INTO day_cards (date, slug, title, body_md, spotlight_json, tags, hero_url) VALUES
