INSERT INTO day_cards (date, slug, title, body_md, spotlight_json, tags, hero_url) VALUES
  ('2026-05-27', 'hemp-the-miracle-plant', 'Hemp: The Miracle Plant', 'Hemp produces strong fibers for textiles and rope, nutritious seeds, CBD, building materials (hempcrete), biofuel, and even plastics. It''s sustainable and versatile.
INSERT INTO day_cards (date, slug, title, body_md, spotlight_json, tags, hero_url) VALUES
  ('2026-05-28', 'maui-wowie-sativa', 'Maui Wowie (Sativa)', 'This Hawaiian sativa landrace brings tropical vibes with pineapple flavors and uplifting, energetic effects. It''s a taste of paradise in plant form.
INSERT INTO day_cards (date, slug, title, body_md, spotlight_json, tags, hero_url) VALUES
