INSERT INTO day_cards (date, slug, title, body_md, spotlight_json, tags, hero_url) VALUES
  ('2026-05-31', 'may-reflection-session', 'May Reflection Session', 'The month is ending. Reflect on your May experiences, what you learned, and how you grew. Use cannabis to facilitate honest, thoughtful introspection.
INSERT INTO day_cards (date, slug, title, body_md, spotlight_json, tags, hero_url) VALUES
