INSERT INTO day_cards (date, slug, title, body_md, spotlight_json, tags, hero_url) VALUES
  ('2026-06-04', 'blue-dream-sativa-dominant', 'Blue Dream (Sativa-Dominant)', 'A California staple, Blue Dream crosses Blueberry with Haze for a sweet berry flavor and balanced high—relaxed but not sedated, uplifted but not racy. It''s beginner-friendly.
INSERT INTO day_cards (date, slug, title, body_md, spotlight_json, tags, hero_url) VALUES
