INSERT INTO day_cards (date, slug, title, body_md, spotlight_json, tags, hero_url) VALUES
  ('2026-06-06', 'hydrology9-water-cooled-vaping', 'Hydrology9: Water-Cooled Vaping', 'Premium vaporizers like the Hydrology9 use water filtration for incredibly smooth vapor. If you can afford it, water-cooled vaping is a game-changer.
INSERT INTO day_cards (date, slug, title, body_md, spotlight_json, tags, hero_url) VALUES
