INSERT INTO day_cards (date, slug, title, body_md, spotlight_json, tags, hero_url) VALUES
  ('2026-06-08', 'cannabis-for-ibs', 'Cannabis for IBS', 'Irritable Bowel Syndrome sufferers often report relief from cannabis, which can reduce inflammation, pain, and cramping. The gut has many cannabinoid receptors.
INSERT INTO day_cards (date, slug, title, body_md, spotlight_json, tags, hero_url) VALUES
