INSERT INTO day_cards (date, slug, title, body_md, spotlight_json, tags, hero_url) VALUES
  ('2026-06-10', 'the-difference-feminized-vs-regular-seeds', 'The Difference: Feminized vs. Regular Seeds', 'Regular seeds produce male or female plants. Feminized seeds produce only females (which produce buds). For home growers, feminized seeds eliminate the need to identify and remove males.
INSERT INTO day_cards (date, slug, title, body_md, spotlight_json, tags, hero_url) VALUES
  ('2026-06-11', 'sunset-sherbet-indica-dominant', 'Sunset Sherbet (Indica-Dominant)', 'Part of the Cookies family, Sunset Sherbet offers sweet, fruity flavors and a potent, full-body high that''s both relaxing and euphoric. It''s a modern classic.
