INSERT INTO list_items (list_id, title, description, why_high, meta_json, order_position) VALUES
(1, 'Hereditary', 'A family grieves the death of their grandmother, but darker secrets begin to surface. Ari Aster''s debut is a slow-burn nightmare that burrows into your brain.', 'The atmospheric dread builds slowly, perfect for heightened senses. Every frame is meticulously crafted to unsettle you, and when elevated, you''ll catch details that make second viewings even more terrifying.', '{"year": 2018, "director": "Ari Aster", "genre": "Psychological Horror", "where_to_watch": "Amazon Prime, Paramount+"}', 1),
(1, 'The Shining', 'Jack Torrance accepts a caretaker job at the isolated Overlook Hotel, where supernatural forces drive him to madness.', 'Kubrick''s masterpiece becomes a sensory experience when high. The symmetrical shots, the color red, the impossible architecture - everything clicks into place in unsettling ways.', '{"year": 1980, "director": "Stanley Kubrick", "genre": "Psychological Horror", "where_to_watch": "Max, Paramount+"}', 2),
(1, 'The Thing', 'An Antarctic research team discovers a parasitic alien that perfectly imitates any living being. Paranoia ensues as they try to determine who is human.', 'The practical effects are mind-blowing, and the paranoia becomes palpable. Cannabis amplifies the tension and makes you question everything, just like the characters.', '{"year": 1982, "director": "John Carpenter", "genre": "Sci-Fi Horror", "where_to_watch": "Peacock, Amazon Prime"}', 3),
(1, 'Midsommar', 'A couple travels to Sweden for a midsummer festival, but the idyllic commune hides disturbing rituals.', 'Bright, beautiful, and deeply disturbing - Midsommar is horror in daylight. The sensory overload of colors, flowers, and ritualistic horror is intensified when elevated.', '{"year": 2019, "director": "Ari Aster", "genre": "Folk Horror", "where_to_watch": "Amazon Prime, Paramount+"}', 4),
(1, 'Get Out', 'A Black man visits his white girlfriend''s family estate, uncovering a disturbing secret about their seemingly liberal community.', 'The social commentary hits harder, and the hypnosis scenes become genuinely unsettling. Jordan Peele packed every frame with meaning that reveals itself when you''re paying attention.', '{"year": 2017, "director": "Jordan Peele", "genre": "Social Horror", "where_to_watch": "Peacock, Amazon Prime"}', 5),
(1, 'A Quiet Place', 'A family lives in silence, hunted by creatures that attack anything that makes sound.', 'The silence becomes deafening when you''re high. Every tiny sound matters, creating an edge-of-your-seat experience where your own heartbeat becomes part of the tension.', '{"year": 2018, "director": "John Krasinski", "genre": "Creature Horror", "where_to_watch": "Paramount+, Amazon Prime"}', 6),
(1, 'The Witch', 'A 1630s New England family faces supernatural evil lurking in the woods beyond their farm.', 'The period-accurate dialogue and slow-burn atmosphere create an immersive historical nightmare. Cannabis helps you sink into the time period and feel the family''s isolation.', '{"year": 2015, "director": "Robert Eggers", "genre": "Folk Horror", "where_to_watch": "Max, Amazon Prime"}', 7),
(1, 'Sinister', 'A true-crime writer discovers a box of home movies depicting brutal murders, putting his own family in danger.', 'The found-footage elements mixed with cinematic storytelling create genuine scares. The soundtrack by Ulver is haunting, and when high, the demonic imagery becomes unforgettable.', '{"year": 2012, "director": "Scott Derrickson", "genre": "Supernatural Horror", "where_to_watch": "Amazon Prime, Apple TV+"}', 8),
(1, 'It Follows', 'After a sexual encounter, a teenager is pursued by a supernatural entity that only she can see.', 'The dream-logic rules and synth score create an inescapable nightmare. Cannabis amplifies the paranoia - is that person in the background really just a background extra?', '{"year": 2014, "director": "David Robert Mitchell", "genre": "Supernatural Horror", "where_to_watch": "Netflix, Amazon Prime"}', 9),
(1, 'Annihilation', 'A biologist enters a mysterious quarantine zone where the laws of nature don''t apply, searching for her missing husband.', 'Not strictly horror, but the trippy visuals and existential dread make it perfect for an elevated viewing. The Shimmer sequences are breathtaking and terrifying.', '{"year": 2018, "director": "Alex Garland", "genre": "Sci-Fi Horror", "where_to_watch": "Paramount+, Hulu"}', 10),
(1, 'The Descent', 'Six women become trapped in a cave system and hunted by underground creatures.', 'The claustrophobia is real. Even before the creatures appear, the cave sequences create intense anxiety. Cannabis makes you feel like you''re right there with them.', '{"year": 2005, "director": "Neil Marshall", "genre": "Creature Horror", "where_to_watch": "Max, Amazon Prime"}', 11),
(1, 'Evil Dead (2013)', 'Five friends unleash demons in a remote cabin by reading from the Book of the Dead.', 'This remake doesn''t hold back on practical effects and gore. It''s a rollercoaster that keeps intensifying, perfect for those who want their horror extreme and unrelenting.', '{"year": 2013, "director": "Fede √Ålvarez", "genre": "Supernatural Horror", "where_to_watch": "Max, Amazon Prime"}', 12),
(1, 'The Conjuring', 'Paranormal investigators help a family terrorized by a dark presence in their farmhouse.', 'Classic haunted house done right. The scares are earned, the atmosphere is thick, and cannabis makes every creak and shadow feel more present.', '{"year": 2013, "director": "James Wan", "genre": "Supernatural Horror", "where_to_watch": "Max, Amazon Prime"}', 13);
